import SearchBar from "../Components/SearchBar";
import Swan from "../Components/Swan";
import SwanResult from "../Components/SwanResult";
import "../styles/Main.scss";
import React, { useEffect, useState } from "react";

import { useForm } from 'react-hook-form';


function Main() {
  const {
    register,
    handleSubmit,
    reset,
    formState,
    formState: { errors },
  } = useForm();

  const [multipleImages, setMultipleImages] = useState([]);
    // Functions to preview multiple images
    const changeMultipleFiles = (e) => {
      if (e.target.files) {
        const imageArray = Array.from(e.target.files).map((file) =>
          URL.createObjectURL(file)
        );
        setMultipleImages((prevImages) => prevImages.concat(imageArray));
      }
    };
    const render = (data) => {
      return data.map((image) => {
        return <img className="image" style={{width: '10px', height: '10px',}} src={image} alt="" key={image} />;
      });
    };

  return (
    <div className="app">
      <div className="bar">
      {/* <p className="putoshka">Putoshka: Swan Scanner</p> */}
      <p className="putoshka">Обработанные фото</p>

      </div>
      <div className="main">
        <div className="left">
          {/* <p className='title'>Обработанные фото</p> */}
          <SearchBar/>
          <div className="swanDataset">
            <Swan/><Swan/><Swan/><Swan/>
          </div>
        </div>
        <div className="right">
          <SwanResult/>
          <input
          className="addSwans" 
          type="file"
          name="file1"
          accept="image/*" 
          multiple
          {...register('file', { required: true })}
          onChange={changeMultipleFiles}
          placeholder="Добавить фотографии"
        />
        {/* error handling with React Hook Form */}
        {errors.file && <p className="error">Please select an image</p>}

        {/* The render function with the multiple image state */}
        {render(multipleImages)}
        </div>
      </div>
    </div>
  );
}

export default Main;
